import { useState, useRef, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { Separator } from "@/components/ui/separator";
import { Hash, Settings, Mic, Headphones, Plus, Gift, Sticker, Smile } from "lucide-react";
import { cn } from "@/lib/utils";
// @ts-ignore
import botAvatar from "@assets/generated_images/cyberpunk_discord_bot_avatar.png";

const ADMIN_ID = "763625050213187614";
const CHANNELS = [
  { id: "general", name: "general", type: "text" as const },
  { id: "catching", name: "catching", type: "text" as const },
  { id: "proofs", name: "proofs", type: "text" as const },
  { id: "spam", name: "spam", type: "text" as const },
];

type Message = {
  id: string;
  content: string;
  authorId: string;
  timestamp: Date;
  isBot: boolean;
  channelId: string;
};

type User = {
  id: string;
  username: string;
  discriminator: string;
  messages: number;
  catches: number;
  pokecoins: number;
  shinyCatches?: number;
  rareShinyCatches?: number;
};

type BotSettings = {
  id: number;
  eventActive: boolean;
  pokecoinRate: number;
  countingChannels: string[];
  proofsChannelId: string | null;
  adminUserId: string;
};

export default function ChatInterface() {
  const [activeChannel, setActiveChannel] = useState("general");
  const [inputValue, setInputValue] = useState("");
  const [localMessages, setLocalMessages] = useState<Message[]>([]);
  const scrollRef = useRef<HTMLDivElement>(null);
  const queryClient = useQueryClient();

  const currentUser = {
    id: ADMIN_ID,
    username: "AdminUser",
    discriminator: "0001",
  };

  // Fetch users
  const { data: users = [] } = useQuery<User[]>({
    queryKey: ["/api/users"],
  });

  // Fetch settings
  const { data: settings } = useQuery<BotSettings>({
    queryKey: ["/api/settings"],
  });

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async (data: { userId: string; username: string; discriminator: string; channelId: string; content: string }) => {
      const response = await fetch("/api/messages", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Failed to send message");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
    },
  });

  // Catch mutation
  const catchMutation = useMutation({
    mutationFn: async (data: { userId: string; username: string; discriminator: string }) => {
      const response = await fetch("/api/catches", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Failed to catch");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
    },
  });

  // Event toggle mutation
  const toggleEventMutation = useMutation({
    mutationFn: async (active: boolean) => {
      const response = await fetch("/api/settings/event", {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ active, adminUserId: currentUser.id }),
      });
      if (!response.ok) throw new Error("Failed to toggle event");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/settings"] });
    },
  });

  // Rate update mutation
  const updateRateMutation = useMutation({
    mutationFn: async (rate: number) => {
      const response = await fetch("/api/settings/pokecoin-rate", {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ rate, adminUserId: currentUser.id }),
      });
      if (!response.ok) throw new Error("Failed to update rate");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/settings"] });
    },
  });

  // Reset user mutation
  const resetUserMutation = useMutation({
    mutationFn: async ({ userId, type }: { userId: string; type: "messages" | "catches" | "all" }) => {
      const response = await fetch(`/api/users/${userId}/reset`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ type, adminUserId: currentUser.id }),
      });
      if (!response.ok) throw new Error("Failed to reset user");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
    },
  });

  // Reset all mutation
  const resetAllMutation = useMutation({
    mutationFn: async (type: "messages" | "catches" | "all") => {
      const response = await fetch("/api/users/reset-all", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ type, adminUserId: currentUser.id }),
      });
      if (!response.ok) throw new Error("Failed to reset all");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
    },
  });

  const createBotMessage = (content: string): Message => ({
    id: Math.random().toString(36).substr(2, 9),
    content,
    authorId: "bot",
    timestamp: new Date(),
    isBot: true,
    channelId: activeChannel,
  });

  const processCommand = async (content: string, authorId: string, channelId: string) => {
    const args = content.trim().split(" ");
    const command = args[0].toLowerCase();

    if (!command.startsWith(".") && !command.startsWith("-")) return null;

    const cmd = command.startsWith(".") ? command.slice(1) : command.slice(1);

    // .help
    if (cmd === "help") {
      return createBotMessage(`** Bot Commands**

** Statistics**
\`.catches\` - View your catch stats
\`.leaderboard messages\` or \`.lb messages\` - Message leaderboard
\`.leaderboard catches\` or \`.lb catches\` - Catch leaderboard
\`.profile\` - Check your stats

** Actions**
\`.withdraw [market_id]\` - Withdraw coins

** Admin Only**
\`.event [on/off]\` - Toggle event
\`.reset [messages/catches/all] [user_id/all]\` - Reset stats
\`.rate [amount]\` - Set Pokecoin rate`);
    }

    // .catches
    if (cmd === "catches") {
      const user = users.find(u => u.id === authorId);
      if (!user) return createBotMessage("You don't have any catches yet!");

      const catchRank = [...users].sort((a, b) => b.catches - a.catches).findIndex(u => u.id === authorId) + 1;
      const totalCatches = users.reduce((sum, u) => sum + u.catches, 0);

      return createBotMessage(`** ${user.username}'s Catches**

 Total Catches: **${user.catches}**
 Shiny Catches: **${user.shinyCatches || 0}**
 Rare Shiny: **${user.rareShinyCatches || 0}**
 Rank: #${catchRank} of ${users.length}
 Pokecoins: ${user.pokecoins}

 Server Total: ${totalCatches} catches`);
    }

    // .leaderboard
    if (cmd === "leaderboard" || cmd === "lb") {
      const lbType = args[1]?.toLowerCase();

      if (lbType === "catches") {
        const sortedUsers = [...users].sort((a, b) => b.catches - a.catches).slice(0, 10);
        const totalCatches = users.reduce((sum, u) => sum + u.catches, 0);
        const lbString = sortedUsers.map((u, i) => {
          const medal = i === 0 ? "" : i === 1 ? "" : i === 2 ? "" : `#${i + 1}`;
          return `${medal} **${u.username}**\n **${u.catches}** catches |  ${u.pokecoins} coins`;
        }).join("\n\n");

        return createBotMessage(`** Catches Leaderboard**\n\n${lbString || "No catches yet!"}\n\n Total Catches: ${totalCatches} |  Active Users: ${users.length}`);
      } else if (lbType === "messages") {
        const sortedUsers = [...users].sort((a, b) => b.messages - a.messages).slice(0, 10);
        const totalMessages = users.reduce((sum, u) => sum + u.messages, 0);
        const lbString = sortedUsers.map((u, i) => {
          const medal = i === 0 ? "" : i === 1 ? "" : i === 2 ? "" : `#${i + 1}`;
          return `${medal} **${u.username}**\n **${u.messages}** messages |  ${u.pokecoins} coins`;
        }).join("\n\n");

        return createBotMessage(`** Messages Leaderboard**\n\n${lbString || "No messages yet!"}\n\n Total Messages: ${totalMessages} |  Active Users: ${users.length}`);
      } else {
        return createBotMessage("Please specify leaderboard type: `.lb messages` or `.lb catches`");
      }
    }

    // .profile
    if (cmd === "profile") {
      const user = users.find(u => u.id === authorId);
      if (!user) return createBotMessage("You don't have any stats yet!");

      const messageRank = [...users].sort((a, b) => b.messages - a.messages).findIndex(u => u.id === authorId) + 1;
      const catchRank = [...users].sort((a, b) => b.catches - a.catches).findIndex(u => u.id === authorId) + 1;

      return createBotMessage(`** ${user.username}'s Profile**\n\n Messages: **${user.messages}** (Rank: #${messageRank})\n Catches: **${user.catches}** (Rank: #${catchRank})\n Pokecoins: ${user.pokecoins}`);
    }

    // .event [on/off]
    if (cmd === "event") {
      if (authorId !== ADMIN_ID) return createBotMessage(" You do not have permission.");

      const state = args[1]?.toLowerCase();
      if (state === "on") {
        toggleEventMutation.mutate(true);
        return createBotMessage(" Event has been turned **ON**.");
      } else if (state === "off") {
        toggleEventMutation.mutate(false);
        return createBotMessage(" Event has been turned **OFF**.");
      }
      return createBotMessage("Usage: `.event on` or `.event off`");
    }

    // .reset
    if (cmd === "reset") {
      if (authorId !== ADMIN_ID) return createBotMessage(" You do not have permission.");

      const targetType = args[1]?.toLowerCase();
      const targetUser = args[2];

      if (!["messages", "catches", "all"].includes(targetType)) {
        return createBotMessage("Usage: `.reset [messages/catches/all] [user_id/all]`");
      }

      if (targetUser === "all") {
        resetAllMutation.mutate(targetType as any);
        return createBotMessage(` Reset **${targetType}** for **all users**.`);
      } else if (targetUser) {
        resetUserMutation.mutate({ userId: targetUser, type: targetType as any });
        return createBotMessage(` Reset **${targetType}** for user **${targetUser}**.`);
      }
      return createBotMessage("Please specify a user ID or 'all'");
    }

    // .rate
    if (cmd === "rate") {
      if (authorId !== ADMIN_ID) return createBotMessage(" You do not have permission.");
      const amount = parseInt(args[1]);
      if (isNaN(amount)) return createBotMessage("Usage: `.rate [number]`");
      updateRateMutation.mutate(amount);
      return createBotMessage(` Pokecoin rate set to **${amount}** coins per milestone.`);
    }

    // .withdraw
    if (cmd === "withdraw") {
      const marketId = args[1];
      if (!marketId) return createBotMessage("Usage: `.withdraw [market_id]`");
      return createBotMessage(`Processing withdrawal for Market ID: ${marketId}...\nPlease pay the bot, then type \`-payed 1\` in the #proofs channel.`);
    }

    // -payed
    if (command.startsWith("-payed")) {
      if (channelId !== "proofs") return createBotMessage(" Please use this command in the #proofs channel.");
      return createBotMessage(" Payment verified! Request submitted.");
    }

    return null;
  };

  const handleSend = async () => {
    if (!inputValue.trim()) return;

    const messageContent = inputValue;
    setInputValue("");

    try {
      const result = await sendMessageMutation.mutateAsync({
        content: messageContent,
        userId: currentUser.id,
        username: currentUser.username,
        discriminator: currentUser.discriminator,
        channelId: activeChannel,
      });

      // Add the saved message to local state
      setLocalMessages((prev) => [...prev, result.message]);

      // Show spam warning if blocked
      if (result.spam?.blocked) {
        console.warn("Message flagged as spam:", result.spam.reason);
      }
    } catch (error) {
      console.error("Failed to send message:", error);
    }
  };

  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [localMessages]);

  const currentChannelName = CHANNELS.find(c => c.id === activeChannel)?.name;
  const currentUserData = users.find(u => u.id === currentUser.id);

  return (
    <div className="flex h-screen w-full bg-[#313338] text-gray-100 font-sans overflow-hidden">

      {/* Server Sidebar */}
      <div className="w-[72px] bg-[#1E1F22] flex flex-col items-center py-3 gap-2 shrink-0">
        <div className="w-12 h-12 rounded-[24px] bg-[#36393f] hover:bg-[#5865F2] hover:rounded-[16px] transition-all cursor-pointer flex items-center justify-center text-white mb-2">
           <span className="text-sm font-bold">DM</span>
        </div>
        <Separator className="w-8 bg-[#35363C] mb-2" />

        <div className="relative group">
            <div className="absolute left-0 top-0 bottom-0 w-1 bg-white rounded-r-full my-auto h-10 -ml-4" />
            <Avatar className="w-12 h-12 rounded-[16px] cursor-pointer">
            <AvatarImage src={botAvatar} />
            <AvatarFallback>SV</AvatarFallback>
            </Avatar>
        </div>

        <div className="w-12 h-12 rounded-[24px] bg-[#36393f] hover:bg-[#3ba55c] hover:rounded-[16px] transition-all cursor-pointer flex items-center justify-center text-green-500 mt-auto">
           <Plus size={24} />
        </div>
      </div>

      {/* Channel Sidebar */}
      <div className="w-60 bg-[#2B2D31] flex flex-col shrink-0">
        <div className="h-12 px-4 flex items-center shadow-sm border-b border-[#1f2023] hover:bg-[#35373C] cursor-pointer transition-colors">
          <h1 className="font-bold text-[15px] truncate">Utility Bot Test Server</h1>
        </div>

        <ScrollArea className="flex-1 px-2 py-3">
            <div className="mb-4">
                <div className="px-2 text-xs font-bold text-[#949BA4] uppercase hover:text-gray-300 cursor-pointer flex items-center gap-1 mb-1">
                    <span className="text-[10px]"></span> Text Channels
                </div>
                <div className="space-y-[2px]">
                    {CHANNELS.map(channel => (
                        <div
                            key={channel.id}
                            onClick={() => setActiveChannel(channel.id)}
                            data-testid={`channel-${channel.id}`}
                            className={cn(
                                "px-2 py-[6px] rounded mx-1 flex items-center gap-1.5 cursor-pointer group",
                                activeChannel === channel.id ? "bg-[#404249] text-white" : "text-[#949BA4] hover:bg-[#35373C] hover:text-gray-200"
                            )}
                        >
                            <Hash className="w-5 h-5 text-[#80848E]" />
                            <span className="font-medium truncate">{channel.name}</span>
                        </div>
                    ))}
                </div>
            </div>
        </ScrollArea>

        {/* User Control Panel */}
        <div className="h-[52px] bg-[#232428] px-2 flex items-center gap-2 shrink-0">
            <Avatar className="w-8 h-8 relative cursor-pointer hover:opacity-80">
                <div className="absolute bottom-0 right-0 w-3 h-3 bg-green-500 rounded-full border-[2px] border-[#232428] z-10"></div>
